import React, { useState, useEffect, useRef } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import {
  Video,
  VideoOff,
  Mic,
  MicOff,
  Phone,
  MessageCircle,
  Send,
  User,
  Globe,
  Settings,
  Wifi,
  WifiOff,
  SkipForward,
  Shield,
  Volume2,
  VolumeX,
  Crown,
  Sparkles, 
  ArrowLeft,
  Zap,
  MoreVertical
} from 'lucide-react';
import toast from 'react-hot-toast';
import ReactCountryFlag from 'react-country-flag';

// minimal country map (keep consistent with WaitingRoom)
const countryCodeMap = {
  'United States': 'US',
  'USA': 'US',
  'United Kingdom': 'GB',
  'UK': 'GB',
  'India': 'IN',
  'Canada': 'CA',
  'Australia': 'AU',
  'Germany': 'DE',
  'France': 'FR',
  'Japan': 'JP',
  'China': 'CN',
  'Brazil': 'BR',
  'Mexico': 'MX',
  'Russia': 'RU',
  'South Korea': 'KR',
  'Korea, South': 'KR',
  'Italy': 'IT',
  'Spain': 'ES',
  'Netherlands': 'NL',
  'Sweden': 'SE',
  'Norway': 'NO',
  'Denmark': 'DK',
  'Finland': 'FI',
  'Poland': 'PL',
  'Turkey': 'TR',
  'South Africa': 'ZA',
  'Israel': 'IL',
  'Thailand': 'TH',
  'Vietnam': 'VN',
  'Philippines': 'PH',
  'Indonesia': 'ID',
  'Malaysia': 'MY',
  'Singapore': 'SG',
  'New Zealand': 'NZ',
  'Ireland': 'IE',
  'Portugal': 'PT',
  'Belgium': 'BE',
  'Switzerland': 'CH',
  'Austria': 'AT',
  'Czech Republic': 'CZ',
};

// helper to get 2-letter code
const getCountryCode = (country) => {
  if (!country) return '';
  if (/^[A-Za-z]{2}$/.test(country)) return country.toUpperCase();
  return countryCodeMap[country] || '';
};

const ChatRoom = ({ socket, user }) => {
  const navigate = useNavigate();
  const location = useLocation();
  const { roomId, matchedUser: stateMatchedUser } = location.state || {};

  const [matchedUser, setMatchedUser] = useState(stateMatchedUser || null);
  const [isVideoOn, setIsVideoOn] = useState(true);
  const [isAudioOn, setIsAudioOn] = useState(true);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [isConnected, setIsConnected] = useState(false);
  const [isSkipping, setIsSkipping] = useState(false);
  const [callDuration, setCallDuration] = useState(0);
  const [showSettings, setShowSettings] = useState(false);
  const [isRemoteVideoOn, setIsRemoteVideoOn] = useState(true);

  const localVideoRef = useRef(null);
  const remoteVideoRef = useRef(null);
  const peerConnectionRef = useRef(null);
  const messagesEndRef = useRef(null);
  const callTimerRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Call timer effect
  useEffect(() => {
    if (isConnected) {
      callTimerRef.current = setInterval(() => {
        setCallDuration(prev => prev + 1);
      }, 1000);
    }

    return () => {
      if (callTimerRef.current) {
        clearInterval(callTimerRef.current);
      }
    };
  }, [isConnected]);

  useEffect(() => {
    if (!socket || !roomId) {
      navigate('/');
      return;
    }

    initializeWebRTC();

    // Socket event listeners
    socket.on('offer', handleOffer);
    socket.on('answer', handleAnswer);
    socket.on('ice-candidate', handleIceCandidate);
    socket.on('receive-message', handleReceiveMessage);
    socket.on('call-ended', handleCallEnded);
    socket.on('skip-matched', handleSkipMatched);
    socket.on('user-video-toggle', handleRemoteVideoToggle);
    socket.on('current-match', handleCurrentMatch);

    return () => {
      socket.off('offer');
      socket.off('answer');
      socket.off('ice-candidate');
      socket.off('receive-message');
      socket.off('call-ended');
      socket.off('skip-matched');
      socket.off('user-video-toggle');
      socket.off('current-match');
    };
  }, [socket, roomId]);

  const initializeWebRTC = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: {
          width: { ideal: 1280 },
          height: { ideal: 720 },
          frameRate: { ideal: 30 }
        },
        audio: {
          echoCancellation: true,
          noiseSuppression: true,
          autoGainControl: true
        }
      });

      if (localVideoRef.current) {
        localVideoRef.current.srcObject = stream;
      }

      // Create peer connection with better configuration
      peerConnectionRef.current = new RTCPeerConnection({
        iceServers: [
          { urls: 'stun:stun.l.google.com:19302' },
          { urls: 'stun:stun1.l.google.com:19302' }
        ],
        iceCandidatePoolSize: 10
      });

      // Add local stream to peer connection
      stream.getTracks().forEach(track => {
        peerConnectionRef.current.addTrack(track, stream);
      });

      // Handle remote stream
      peerConnectionRef.current.ontrack = (event) => {
        if (remoteVideoRef.current) {
          remoteVideoRef.current.srcObject = event.streams[0];
          setIsRemoteVideoOn(true);
        }
      };

      // Handle ICE candidates
      peerConnectionRef.current.onicecandidate = (event) => {
        if (event.candidate) {
          socket.emit('ice-candidate', {
            candidate: event.candidate,
            roomId: roomId
          });
        }
      };

      // Handle connection state changes
      peerConnectionRef.current.onconnectionstatechange = () => {
        const connectionState = peerConnectionRef.current.connectionState;
        if (connectionState === 'connected') {
          setIsConnected(true);
          toast.success('Connection established!');
        } else if (connectionState === 'disconnected' || connectionState === 'failed') {
          setIsConnected(false);
          toast.error('Connection lost');
        }
      };

      setIsConnected(true);
    } catch (error) {
      console.error('Error accessing media devices:', error);
      toast.error('Unable to access camera/microphone. Please check permissions.');
    }
  };

  const handleOffer = async (data) => {
    try {
      await peerConnectionRef.current.setRemoteDescription(data.offer);
      const answer = await peerConnectionRef.current.createAnswer();
      await peerConnectionRef.current.setLocalDescription(answer);
      
      socket.emit('answer', {
        answer: answer,
        roomId: roomId
      });
    } catch (error) {
      console.error('Error handling offer:', error);
    }
  };

  const handleAnswer = async (data) => {
    try {
      await peerConnectionRef.current.setRemoteDescription(data.answer);
    } catch (error) {
      console.error('Error handling answer:', error);
    }
  };

  const handleIceCandidate = async (data) => {
    try {
      await peerConnectionRef.current.addIceCandidate(data.candidate);
    } catch (error) {
      console.error('Error handling ICE candidate:', error);
    }
  };

  const handleReceiveMessage = (data) => {
    setMessages(prev => [...prev, data]);
  };

  const handleCallEnded = () => {
    toast.success('Call ended');
    navigate('/waiting');
  };

  const handleSkipMatched = (data) => {
    setIsSkipping(false);
    toast.dismiss('skipping');
    toast.success('Connected to new match!');
    navigate('/chat', { 
      state: { roomId: data.roomId, matchedUser: data.matchedUser },
      replace: true 
    });
  };

  const handleRemoteVideoToggle = (data) => {
    setIsRemoteVideoOn(data.videoOn);
  };

  const handleCurrentMatch = (data) => {
    if (data?.matchedUser) {
      setMatchedUser(data.matchedUser);
    } else {
      toast.warn('Matched user details not available');
    }
  };

  const toggleVideo = () => {
    if (localVideoRef.current?.srcObject) {
      const videoTrack = localVideoRef.current.srcObject.getVideoTracks()[0];
      if (videoTrack) {
        videoTrack.enabled = !videoTrack.enabled;
        setIsVideoOn(videoTrack.enabled);
        
        // Notify other user
        socket.emit('video-toggle', {
          roomId: roomId,
          videoOn: videoTrack.enabled
        });
      }
    }
  };

  const toggleAudio = () => {
    if (localVideoRef.current?.srcObject) {
      const audioTrack = localVideoRef.current.srcObject.getAudioTracks()[0];
      if (audioTrack) {
        audioTrack.enabled = !audioTrack.enabled;
        setIsAudioOn(audioTrack.enabled);
      }
    }
  };

  const sendMessage = () => {
    if (!newMessage.trim()) return;

    const messageData = {
      text: newMessage,
      sender: user.isAnonymous ? 'Anonymous' : user.name,
      senderId: user.id,
      timestamp: new Date().toISOString(),
      roomId: roomId
    };

    socket.emit('send-message', messageData);
    setMessages(prev => [...prev, messageData]);
    setNewMessage('');
  };

  const endCall = () => {
    if (peerConnectionRef.current) {
      peerConnectionRef.current.close();
    }
    socket.emit('end-call', { roomId: roomId });
    navigate('/waiting');
  };

  const handleBack = () => {
    navigate(-1);
  };

  const handleSkip = () => {
    if (isSkipping) return;
    setIsSkipping(true);
    socket.emit('skip-chat', { roomId: roomId });
    toast.loading('Finding your next amazing match...', { 
      id: 'skipping',
      duration: Infinity 
    });
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  const formatDuration = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  const floatingAnimation = {
    animate: {
      y: [0, -8, 0],
      transition: {
        duration: 3,
        repeat: Infinity,
        ease: "easeInOut"
      }
    }
  };

  return (
    <div className="min-h-screen flex flex-col bg-gray-100 relative overflow-hidden">

      {/* Header */}
      <motion.div
        className="bg-white px-6 py-4 flex justify-between items-center border-b border-gray-200 shadow-lg relative z-10"
        initial={{ y: -50, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ duration: 0.5 }}
      >
        <div className="flex items-center gap-4 text-gray-900">
          <motion.div
            className="relative"
            whileHover={{ scale: 1.1 }}
            transition={{ duration: 0.2 }}
          >
            <div className="w-14 h-14 bg-blue-600 rounded-2xl flex items-center justify-center text-white font-bold text-xl shadow-lg">
              {matchedUser?.isAnonymous ? 'A' : (matchedUser?.name?.[0]?.toUpperCase() || 'U')}
            </div>
            <div className="absolute -bottom-1 -right-1 w-4 h-4 bg-green-400 rounded-full border-2 border-white"></div>
          </motion.div>

          <div>
            <h3 className="text-xl font-bold flex items-center gap-2 text-gray-900">
              {matchedUser?.isAnonymous ? 'Anonymous User' : matchedUser?.name || 'Unknown User'}
              <Shield size={16} className="text-blue-600" />
            </h3>
            <div className="flex items-center gap-3 text-sm text-gray-600">
              <div className="flex items-center gap-1">
                <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse"></div>
                <span>Connected • {formatDuration(callDuration)}</span>
              </div>
              {matchedUser?.country && (
                <div className="flex items-center gap-1">
                  <Globe size={12} />
                  <span>{matchedUser.country}</span>
                </div>
              )}
            </div>
          </div>
        </div>

        <div className="flex items-center gap-3">
          {/* Connection Status */}
          <motion.div
            className={`flex items-center gap-2 px-3 py-2 rounded-full text-sm ${
              isConnected
                ? 'bg-green-100 text-green-700 border border-green-200'
                : 'bg-red-100 text-red-700 border border-red-200'
            }`}
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.5 }}
          >
            {isConnected ? <Wifi size={16} /> : <WifiOff size={16} />}
            <span>{isConnected ? 'Connected' : 'Connecting'}</span>
          </motion.div>

          {/* Control Buttons */}
          <div className="flex gap-2">
            <motion.button
              onClick={handleBack}
              className="w-12 h-12 rounded-2xl flex items-center justify-center transition-all duration-200 bg-gray-100 hover:bg-gray-200 text-gray-700"
              whileHover={{ scale: 1.1, y: -2 }}
              whileTap={{ scale: 0.9 }}
            >
              <ArrowLeft size={20} />
            </motion.button>

            <motion.button
              onClick={toggleVideo}
              className={`w-12 h-12 rounded-2xl flex items-center justify-center transition-all duration-200 ${
                isVideoOn
                  ? 'bg-green-600 hover:bg-green-700 text-white'
                  : 'bg-red-600 hover:bg-red-700 text-white'
              }`}
              whileHover={{ scale: 1.1, y: -2 }}
              whileTap={{ scale: 0.9 }}
            >
              {isVideoOn ? <Video size={20} /> : <VideoOff size={20} />}
            </motion.button>

            <motion.button
              onClick={toggleAudio}
              className={`w-12 h-12 rounded-2xl flex items-center justify-center transition-all duration-200 ${
                isAudioOn
                  ? 'bg-green-600 hover:bg-green-700 text-white'
                  : 'bg-red-600 hover:bg-red-700 text-white'
              }`}
              whileHover={{ scale: 1.1, y: -2 }}
              whileTap={{ scale: 0.9 }}
            >
              {isAudioOn ? <Mic size={20} /> : <MicOff size={20} />}
            </motion.button>

            <motion.button
              onClick={endCall}
              className="w-12 h-12 bg-red-600 hover:bg-red-700 rounded-2xl flex items-center justify-center text-white transition-all duration-200"
              whileHover={{ scale: 1.1, y: -2 }}
              whileTap={{ scale: 0.9 }}
            >
              <Phone size={20} />
            </motion.button>
          </div>
        </div>
      </motion.div>

      {/* Main Content */}
      <div className="flex-1 grid lg:grid-cols-3 gap-6 p-6 relative z-10">
        {/* Video Section */}
        <div className="lg:col-span-2 flex flex-col gap-6">
          {/* Remote Video */}
          <motion.div
            className="bg-white rounded-3xl overflow-hidden relative flex-1 min-h-[500px] flex items-center justify-center shadow-lg border border-gray-200"
            initial={{ scale: 0.9, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            transition={{ duration: 0.6 }}
          >
            <video
              ref={remoteVideoRef}
              autoPlay
              playsInline
              className="w-full h-full object-cover"
            />

            {!isRemoteVideoOn && (
              <div className="absolute inset-0 flex flex-col items-center justify-center text-gray-600 text-center p-8 bg-gray-100">
                <VideoOff size={64} className="mb-4 text-gray-400" />
                <p className="text-xl font-semibold mb-2">Camera is off</p>
                <p className="text-gray-500">The other user has turned off their camera</p>
              </div>
            )}

            {/* Video Overlay Info */}
            <div className="absolute top-4 left-4 bg-white/90 backdrop-blur-sm px-3 py-2 rounded-full text-gray-900 text-sm flex items-center gap-2 shadow-lg">
              <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse"></div>
              <span>Live • {formatDuration(callDuration)}</span>
            </div>
          </motion.div>

          {/* Local Video */}
          <motion.div
            className="bg-white rounded-2xl overflow-hidden relative w-64 h-48 self-end shadow-lg border border-gray-200"
            initial={{ x: 100, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            transition={{ duration: 0.6, delay: 0.3 }}
            whileHover={{ scale: 1.05 }}
          >
            <video
              ref={localVideoRef}
              autoPlay
              muted
              playsInline
              className="w-full h-full object-cover"
            />

            {!isVideoOn && (
              <div className="absolute inset-0 flex items-center justify-center bg-gray-100">
                <VideoOff size={32} className="text-gray-400" />
              </div>
            )}

            <div className="absolute bottom-2 left-2 bg-white/90 backdrop-blur-sm px-2 py-1 rounded text-gray-900 text-xs shadow-lg">
              You {isVideoOn ? '• Live' : '• Camera off'}
            </div>
          </motion.div>
        </div>

        {/* Chat Section */}
      </div>

      {/* Bottom Controls */}
      <motion.div
        className="flex justify-center gap-4 p-6 relative z-10"
        initial={{ y: 50, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ duration: 0.5, delay: 0.6 }}
      >
        {[
        ].map((control, index) => (
          <motion.button
            key={index}
            onClick={control.onClick}
            className={`flex items-center gap-3 px-6 py-3 rounded-2xl font-semibold transition-all duration-200 backdrop-blur-sm ${
              control.active
                ? 'bg-green-500/90 hover:bg-green-600 text-white'
                : 'bg-red-500/90 hover:bg-red-600 text-white'
            }`}
            whileHover={{ scale: 1.05, y: -2 }}
            whileTap={{ scale: 0.95 }}
          >
            <control.icon size={20} />
            <span>{control.label}</span>
          </motion.button>
        ))}
      </motion.div>
    </div>
  );
};

export default ChatRoom;
