import React, { useState } from 'react';
import toast from 'react-hot-toast';
import { useNavigate } from 'react-router-dom';

export default function Support() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const API = process.env.REACT_APP_API_URL || 'http://localhost:5000';

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!email || !message) {
      toast.error('Please provide an email and a short message');
      return;
    }
    setLoading(true);
    try {
      const res = await fetch(`${API}/api/support`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ name, email, message }),
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data?.message || 'Failed to send message');
      toast.success('Message sent — we will contact you soon');
      setName(''); setEmail(''); setMessage('');
      navigate('/');
    } catch (err) {
      console.error('Support submit error', err);
      toast.error(err.message || 'Unable to send message');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-3xl mx-auto p-6 mt-8 bg-white/5 rounded-lg backdrop-blur-sm">
      <h1 className="text-2xl font-semibold text-white mb-2">Help & Support</h1>
      <p className="text-sm text-gray-300 mb-6">
        Need help? Browse the quick answers below or send us a message using the form.
      </p>

      <section className="mb-6">
        <h2 className="text-lg text-white font-medium mb-2">FAQ</h2>
        <ul className="text-sm text-gray-300 space-y-2">
          <li><strong>How do I find a match?</strong> — Click "Find a Match" from the header or waiting room.</li>
          <li><strong>Is video moderated?</strong> — Admins can monitor and take action via the dashboard.</li>
          <li><strong>How do I report abuse?</strong> — Use the contact form below and include the user ID or time.</li>
        </ul>
      </section>

      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          className="w-full p-3 rounded bg-white/5 text-white"
          placeholder="Your name (optional)"
          value={name}
          onChange={e => setName(e.target.value)}
        />
        <input
          className="w-full p-3 rounded bg-white/5 text-white"
          placeholder="Email"
          type="email"
          value={email}
          onChange={e => setEmail(e.target.value)}
          required
        />
        <textarea
          className="w-full p-3 rounded bg-white/5 text-white"
          placeholder="How can we help? Please include details and any user IDs or timestamps for reports."
          value={message}
          onChange={e => setMessage(e.target.value)}
          rows={6}
          required
        />
        <div className="flex items-center gap-3">
          <button
            type="submit"
            disabled={loading}
            className="px-4 py-2 rounded bg-purple-600 hover:bg-purple-700 disabled:opacity-50"
          >
            {loading ? 'Sending…' : 'Send Message'}
          </button>
          <button
            type="button"
            onClick={() => navigate(-1)}
            className="px-4 py-2 rounded bg-gray-700 hover:bg-gray-600"
          >
            Back
          </button>
        </div>
      </form>
    </div>
  );
}
