import React, { useState, useEffect } from 'react';
import toast from 'react-hot-toast';

export default function Support() {
  const [supportRequests, setSupportRequests] = useState([]);
  const [loading, setLoading] = useState(true);
  const API = process.env.REACT_APP_API_URL || 'http://localhost:5000';

  useEffect(() => {
    fetchSupportRequests();
  }, []);

  const fetchSupportRequests = async () => {
    try {
      const token = localStorage.getItem('adminToken');
      const res = await fetch(`${API}/api/admin/support-requests`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      if (!res.ok) throw new Error('Failed to fetch support requests');
      const data = await res.json();
      setSupportRequests(data);
    } catch (err) {
      console.error('Fetch support requests error', err);
      toast.error('Failed to load support requests');
    } finally {
      setLoading(false);
    }
  };

  const updateStatus = async (id, status, adminNotes) => {
    try {
      const token = localStorage.getItem('adminToken');
      const res = await fetch(`${API}/api/admin/support-requests/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ status, adminNotes }),
      });
      if (!res.ok) throw new Error('Failed to update status');
      toast.success('Status updated successfully');
      fetchSupportRequests(); // Refresh list
    } catch (err) {
      console.error('Update status error', err);
      toast.error('Failed to update status');
    }
  };

  if (loading) return <div className="text-white">Loading...</div>;

  return (
    <div className="max-w-7xl mx-auto p-6 mt-8 bg-white/5 rounded-lg backdrop-blur-sm">
      <h1 className="text-2xl font-semibold text-white mb-6">Support Requests Management</h1>
      {supportRequests.length === 0 ? (
        <p className="text-gray-300">No support requests found.</p>
      ) : (
        <div className="overflow-x-auto">
          <table className="w-full bg-white/10 rounded-lg overflow-hidden">
            <thead className="bg-white/20">
              <tr>
                <th className="px-4 py-3 text-left text-white font-medium">Name</th>
                <th className="px-4 py-3 text-left text-white font-medium">Email</th>
                <th className="px-4 py-3 text-left text-white font-medium">Date</th>
                <th className="px-4 py-3 text-left text-white font-medium">Status</th>
                <th className="px-4 py-3 text-left text-white font-medium">Message</th>
                <th className="px-4 py-3 text-left text-white font-medium">Admin Notes</th>
                <th className="px-4 py-3 text-left text-white font-medium">Actions</th>
              </tr>
            </thead>
            <tbody>
              {supportRequests.map((request) => (
                <tr key={request._id} className="border-t border-white/10 hover:bg-white/5">
                  <td className="px-4 py-3 text-white">{request.name || 'Anonymous'}</td>
                  <td className="px-4 py-3 text-gray-300">{request.email}</td>
                  <td className="px-4 py-3 text-gray-400 text-sm">
                    {new Date(request.createdAt).toLocaleString()}
                  </td>
                  <td className="px-4 py-3">
                    <select
                      value={request.status}
                      onChange={(e) => updateStatus(request._id, e.target.value, request.adminNotes)}
                      className="bg-gray-700 text-white px-2 py-1 rounded text-sm"
                    >
                      <option value="pending">Pending</option>
                      <option value="in-progress">In Progress</option>
                      <option value="resolved">Resolved</option>
                      <option value="closed">Closed</option>
                    </select>
                  </td>
                  <td className="px-4 py-3 text-gray-200 max-w-xs truncate" title={request.message}>
                    {request.message}
                  </td>
                  <td className="px-4 py-3">
                    <textarea
                      value={request.adminNotes || ''}
                      onChange={(e) => {
                        const updatedRequests = supportRequests.map(req =>
                          req._id === request._id ? { ...req, adminNotes: e.target.value } : req
                        );
                        setSupportRequests(updatedRequests);
                      }}
                      onBlur={() => updateStatus(request._id, request.status, request.adminNotes)}
                      className="w-full p-2 rounded bg-gray-700 text-white text-sm resize-none"
                      rows={2}
                      placeholder="Add notes..."
                    />
                  </td>
                  <td className="px-4 py-3">
                    <button
                      onClick={() => updateStatus(request._id, request.status, request.adminNotes)}
                      className="px-3 py-1 bg-blue-600 hover:bg-blue-700 text-white text-sm rounded"
                    >
                      Update
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}
