import React, { useState, useEffect, useRef } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import {
  Video,
  VideoOff,
  Mic,
  MicOff,
  Phone,
  MessageCircle,
  Send,
  User,
  Globe,
  Settings,
  Wifi,
  WifiOff,
  SkipForward
} from 'lucide-react';
import toast from 'react-hot-toast';


const ChatRoom = ({ socket, user }) => {
  const navigate = useNavigate();
  const location = useLocation();
  const { roomId, matchedUser } = location.state || {};

  const [isVideoOn, setIsVideoOn] = useState(true);
  const [isAudioOn, setIsAudioOn] = useState(true);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [isConnected, setIsConnected] = useState(false);

  const localVideoRef = useRef(null);
  const remoteVideoRef = useRef(null);
  const peerConnectionRef = useRef(null);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  useEffect(() => {
    if (!socket || !roomId) {
      navigate('/');
      return;
    }

    // Initialize WebRTC
    initializeWebRTC();

    // Socket event listeners
    socket.on('offer', handleOffer);
    socket.on('answer', handleAnswer);
    socket.on('ice-candidate', handleIceCandidate);
    socket.on('receive-message', handleReceiveMessage);
    socket.on('call-ended', handleCallEnded);

    return () => {
      socket.off('offer');
      socket.off('answer');
      socket.off('ice-candidate');
      socket.off('receive-message');
      socket.off('call-ended');
    };
  }, [socket, roomId]);

  const initializeWebRTC = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: true
      });

      if (localVideoRef.current) {
        localVideoRef.current.srcObject = stream;
      }

      // Create peer connection
      peerConnectionRef.current = new RTCPeerConnection({
        iceServers: [{ urls: 'stun:stun.l.google.com:19302' }]
      });

      // Add local stream to peer connection
      stream.getTracks().forEach(track => {
        peerConnectionRef.current.addTrack(track, stream);
      });

      // Handle remote stream
      peerConnectionRef.current.ontrack = (event) => {
        if (remoteVideoRef.current) {
          remoteVideoRef.current.srcObject = event.streams[0];
        }
      };

      // Handle ICE candidates
      peerConnectionRef.current.onicecandidate = (event) => {
        if (event.candidate) {
          socket.emit('ice-candidate', {
            candidate: event.candidate,
            roomId: roomId
          });
        }
      };

      setIsConnected(true);
    } catch (error) {
      console.error('Error accessing media devices:', error);
      toast.error('Unable to access camera/microphone');
    }
  };

  const handleOffer = async (data) => {
    try {
      await peerConnectionRef.current.setRemoteDescription(data.offer);
      const answer = await peerConnectionRef.current.createAnswer();
      await peerConnectionRef.current.setLocalDescription(answer);
      
      socket.emit('answer', {
        answer: answer,
        roomId: roomId
      });
    } catch (error) {
      console.error('Error handling offer:', error);
    }
  };

  const handleAnswer = async (data) => {
    try {
      await peerConnectionRef.current.setRemoteDescription(data.answer);
    } catch (error) {
      console.error('Error handling answer:', error);
    }
  };

  const handleIceCandidate = async (data) => {
    try {
      await peerConnectionRef.current.addIceCandidate(data.candidate);
    } catch (error) {
      console.error('Error handling ICE candidate:', error);
    }
  };

  const handleReceiveMessage = (data) => {
    setMessages(prev => [...prev, data]);
  };

  const handleCallEnded = () => {
    toast.success('Call ended');
    navigate('/waiting');
  };

  const toggleVideo = () => {
    if (localVideoRef.current?.srcObject) {
      const videoTrack = localVideoRef.current.srcObject.getVideoTracks()[0];
      if (videoTrack) {
        videoTrack.enabled = !videoTrack.enabled;
        setIsVideoOn(videoTrack.enabled);
      }
    }
  };

  const toggleAudio = () => {
    if (localVideoRef.current?.srcObject) {
      const audioTrack = localVideoRef.current.srcObject.getAudioTracks()[0];
      if (audioTrack) {
        audioTrack.enabled = !audioTrack.enabled;
        setIsAudioOn(audioTrack.enabled);
      }
    }
  };

  const sendMessage = () => {
    if (!newMessage.trim()) return;

    const messageData = {
      text: newMessage,
      sender: user.isAnonymous ? 'Anonymous' : user.name,
      timestamp: new Date().toISOString(),
      roomId: roomId
    };

    socket.emit('send-message', messageData);
    setMessages(prev => [...prev, messageData]);
    setNewMessage('');
  };

  const endCall = () => {
    socket.emit('end-call', { roomId: roomId });
    navigate('/waiting');
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      sendMessage();
    }
  };

  return (
    <div className="min-h-screen flex flex-direction flex-col bg-gradient-to-br from-primary-500 via-primary-600 to-secondary-600">
      {/* Header */}
      <div className="glass-strong px-6 py-4 flex justify-between items-center border-b border-white/20">
        <div className="flex items-center gap-4 text-white">
          <div className="w-12 h-12 bg-gradient-to-r from-primary-500 to-secondary-500 rounded-full flex items-center justify-center text-white font-bold text-lg">
            {matchedUser?.isAnonymous ? 'A' : (matchedUser?.name?.[0] || 'U')}
          </div>
          <div>
            <h3 className="text-lg font-semibold">
              {matchedUser?.isAnonymous ? 'Anonymous User' : matchedUser?.name || 'Unknown User'}
            </h3>
            <div className="flex items-center gap-2 text-sm text-white/80">
              <div className="w-2 h-2 bg-green-400 rounded-full"></div>
              <span>Connected</span>
            </div>
          </div>
        </div>

        <div className="flex gap-3">
          <motion.button
            onClick={toggleVideo}
            className={`w-12 h-12 rounded-full flex items-center justify-center transition-all duration-200 ${
              isVideoOn ? 'bg-green-500 hover:bg-green-600' : 'bg-red-500 hover:bg-red-600'
            }`}
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
          >
            {isVideoOn ? <Video size={20} className="text-white" /> : <VideoOff size={20} className="text-white" />}
          </motion.button>

          <motion.button
            onClick={toggleAudio}
            className={`w-12 h-12 rounded-full flex items-center justify-center transition-all duration-200 ${
              isAudioOn ? 'bg-green-500 hover:bg-green-600' : 'bg-red-500 hover:bg-red-600'
            }`}
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
          >
            {isAudioOn ? <Mic size={20} className="text-white" /> : <MicOff size={20} className="text-white" />}
          </motion.button>

          <motion.button
            onClick={endCall}
            className="w-12 h-12 bg-red-500 hover:bg-red-600 rounded-full flex items-center justify-center transition-all duration-200"
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
          >
            <Phone size={20} className="text-white" />
          </motion.button>
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-1 grid lg:grid-cols-3 gap-4 p-4">
        {/* Video Section */}
        <div className="lg:col-span-2 card-glass flex flex-col">
          <div className="flex-1 bg-black rounded-2xl overflow-hidden relative min-h-[400px] flex items-center justify-center">
            <video
              ref={localVideoRef}
              autoPlay
              muted
              playsInline
              className="w-full h-full object-cover"
            />
            {!isVideoOn && (
              <div className="absolute inset-0 flex flex-col items-center justify-center text-white/60 text-center p-8">
                <VideoOff size={48} className="mb-4" />
                <p className="text-lg">Camera is off</p>
              </div>
            )}
          </div>

          <div className="flex justify-center gap-4 mt-4">
            <motion.button
              onClick={toggleVideo}
              className={`w-14 h-14 rounded-full flex items-center justify-center transition-all duration-200 ${
                isVideoOn ? 'bg-green-500 hover:bg-green-600' : 'bg-red-500 hover:bg-red-600'
              }`}
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
            >
              {isVideoOn ? <Video size={24} className="text-white" /> : <VideoOff size={24} className="text-white" />}
            </motion.button>

            <motion.button
              onClick={toggleAudio}
              className={`w-14 h-14 rounded-full flex items-center justify-center transition-all duration-200 ${
                isAudioOn ? 'bg-green-500 hover:bg-green-600' : 'bg-red-500 hover:bg-red-600'
              }`}
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
            >
              {isAudioOn ? <Mic size={24} className="text-white" /> : <MicOff size={24} className="text-white" />}
            </motion.button>
          </div>
        </div>

        {/* Chat Section */}
        <div className="card-glass flex flex-col h-[500px]">
          <div className="px-4 py-3 border-b border-white/20 flex items-center gap-2 text-white font-semibold">
            <MessageCircle size={20} />
            <span>Chat</span>
          </div>

          <div className="flex-1 p-4 overflow-y-auto scrollbar-thin space-y-3">
            <AnimatePresence>
              {messages.map((message, index) => {
                const isOwn = message.sender === (user.isAnonymous ? 'Anonymous' : user.name);
                return (
                  <motion.div
                    key={index}
                    className={`flex flex-col gap-1 ${isOwn ? 'items-end' : 'items-start'}`}
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.3 }}
                  >
                    <div
                      className={`px-4 py-2 rounded-2xl max-w-[80%] ${
                        isOwn
                          ? 'bg-gradient-to-r from-primary-500 to-secondary-500 text-white'
                          : 'glass-strong text-white'
                      }`}
                    >
                      {message.text}
                    </div>
                    <div className="text-xs text-white/60 px-2">
                      {message.sender} â€¢ {new Date(message.timestamp).toLocaleTimeString()}
                    </div>
                  </motion.div>
                );
              })}
            </AnimatePresence>
            <div ref={messagesEndRef} />
          </div>

          <div className="p-4 border-t border-white/20 flex gap-3">
            <input
              type="text"
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="Type a message..."
              className="flex-1 input-glass"
            />
            <motion.button
              onClick={sendMessage}
              className="w-12 h-12 bg-gradient-to-r from-primary-500 to-secondary-500 rounded-full flex items-center justify-center text-white hover:scale-105 transition-all duration-200"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <Send size={20} />
            </motion.button>
          </div>
        </div>
      </div>

      {/* Connection Status */}
      {!isConnected && (
        <div className="fixed top-4 right-4 glass-strong px-4 py-2 rounded-lg text-white text-sm flex items-center gap-2 z-50">
          <WifiOff size={16} className="text-red-400" />
          <span>Connecting...</span>
        </div>
      )}
    </div>
  );
};

export default ChatRoom;
