import React from 'react';
import { useNavigate } from 'react-router-dom';
import { Video, Menu } from 'lucide-react';

const Header = ({ className = '' }) => {
  const navigate = useNavigate();

  return (
    <header className={`w-full glass-strong px-4 sm:px-6 py-3 flex items-center justify-between gap-4 ${className}`}>
      <div className="flex items-center gap-3">
        <div
          onClick={() => navigate('/')}
          className="cursor-pointer inline-flex items-center gap-2"
          title="Home"
        >
        
          <div className="hidden sm:block">
            <div className="text-white font-bold">VideoChat</div>
            <div className="text-xs text-white/60">Connect instantly</div>
          </div>
        </div>
      </div>

      <nav className="flex items-center gap-3">
        <button
          onClick={() => navigate('/waiting')}
          className="btn-ghost hidden sm:inline-flex"
        >
          Find a Match
        </button>

        <button
          onClick={() => window.scrollTo({ top: document.body.scrollHeight, behavior: 'smooth' })}
          className="btn-ghost hidden md:inline-flex"
        >
          About
        </button>

        <button
          onClick={() => window.history.back()}
          className="w-10 h-10 rounded-xl bg-white/5 text-white flex items-center justify-center sm:hidden"
          title="Menu"
        >
          <Menu size={18} />
        </button>
      </nav>
    </header>
  );
};

export default Header;
