import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import { Video, Users, Globe, Shield, Wifi, WifiOff, ArrowRight, ArrowLeft, Sparkles, Star, Zap } from 'lucide-react';
import toast from 'react-hot-toast';

const LandingPage = ({ onUserJoin, isConnected }) => {
  const navigate = useNavigate();
  const [currentStep, setCurrentStep] = useState(1);
  const [isHovered, setIsHovered] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    country: '',
    gender: '',
    isAnonymous: false
  });

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleNext = () => {
    if (!formData.isAnonymous && !formData.name.trim()) {
      toast.error('Please enter your name or select anonymous');
      return;
    }

    if (!formData.isAnonymous && !formData.gender) {
      toast.error('Please select your gender');
      return;
    }

    setCurrentStep(2);
  };

  const handleBack = () => {
    setCurrentStep(1);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!isConnected) {
      toast.error('Not connected to server');
      return;
    }

    if (!formData.isAnonymous) {
      if (!formData.name.trim()) {
        toast.error('Please enter your name or check "Join as Anonymous".');
        return;
      }
      if (!formData.gender) {
        toast.error('Please select your gender.');
        return;
      }
    }

    onUserJoin(formData);
    navigate('/waiting');
    toast.success('Successfully joined!');
  };

  const floatingIcons = [
    { icon: Video, x: '10%', y: '20%', delay: 0 },
    { icon: Users, x: '85%', y: '30%', delay: 0.5 },
    { icon: Globe, x: '15%', y: '70%', delay: 1 },
    { icon: Shield, x: '80%', y: '65%', delay: 1.5 },
  ];

  return (
    <div className="min-h-screen flex items-center justify-center p-4 sm:p-8 relative overflow-hidden">
      {/* Animated Background */}
      <div className="absolute inset-0 bg-gradient-to-br from-purple-900 via-blue-900 to-indigo-900">
        <div className="absolute inset-0 bg-[radial-gradient(ellipse_at_center,_var(--tw-gradient-stops))] from-transparent via-black/20 to-black/60"></div>
        
        {/* Floating Icons */}
        {floatingIcons.map((item, index) => (
          <motion.div
            key={index}
            className="absolute text-white/10"
            style={{ left: item.x, top: item.y }}
            initial={{ y: 0, opacity: 0.3 }}
            animate={{ 
              y: [0, -20, 0],
              opacity: [0.3, 0.6, 0.3],
            }}
            transition={{
              duration: 4,
              delay: item.delay,
              repeat: Infinity,
              ease: "easeInOut"
            }}
          >
            <item.icon size={32} />
          </motion.div>
        ))}
        
        {/* Animated Particles */}
        {[...Array(20)].map((_, i) => (
          <motion.div
            key={i}
            className="absolute w-1 h-1 bg-white/30 rounded-full"
            style={{
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
            }}
            animate={{
              y: [0, -100, 0],
              opacity: [0, 1, 0],
            }}
            transition={{
              duration: 3 + Math.random() * 2,
              repeat: Infinity,
              delay: Math.random() * 2,
            }}
          />
        ))}
      </div>

      <div className="max-w-7xl w-full grid lg:grid-cols-2 gap-8 lg:gap-16 items-center relative z-10">
        {/* Left Section */}
        <motion.div
          className="text-white space-y-6 lg:space-y-8"
          initial={{ opacity: 0, x: -50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.8 }}
        >
          {/* Header with Sparkles */}
          <div className="space-y-6 relative">
            <motion.div
              className="flex items-center gap-2 text-cyan-300 mb-4"
              initial={{ opacity: 0, scale: 0 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ delay: 0.5 }}
            >
              <Sparkles size={24} />
              <span className="font-semibold text-lg">Meet People Worldwide</span>
            </motion.div>

            <motion.h1
              className="text-4xl sm:text-5xl lg:text-6xl font-bold leading-tight"
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8 }}
            >
              <span className="bg-gradient-to-r from-cyan-400 via-blue-500 to-purple-600 bg-clip-text text-transparent">
                Connect
              </span>
              <br />
              <span className="bg-gradient-to-r from-purple-400 to-pink-600 bg-clip-text text-transparent">
                with the World
              </span>
            </motion.h1>

            <motion.p
              className="text-lg sm:text-xl text-white/90 leading-relaxed max-w-lg bg-white/5 backdrop-blur-sm p-6 rounded-2xl border border-white/10"
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.2 }}
            >
              Meet new people through <span className="text-cyan-300 font-semibold">HD video chat</span>. Connect with users from around the globe, share experiences, and make meaningful connections in a secure environment.
            </motion.p>
          </div>

          {/* Features Grid */}
          <motion.div
            className="grid grid-cols-1 sm:grid-cols-2 gap-4"
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.4 }}
          >
            {[
              { icon: Video, text: "HD Video Chat", color: "from-cyan-400 to-blue-500" },
              { icon: Users, text: "Global Community", color: "from-purple-400 to-pink-500" },
              { icon: Globe, text: "Worldwide Access", color: "from-green-400 to-cyan-500" },
              { icon: Shield, text: "Safe & Secure", color: "from-orange-400 to-red-500" },
            ].map((feature, index) => (
              <motion.div
                key={index}
                className="flex items-center gap-4 p-4 rounded-xl bg-white/5 backdrop-blur-sm border border-white/10 hover:bg-white/10 transition-all duration-300 group"
                whileHover={{ scale: 1.05, y: -5 }}
                whileTap={{ scale: 0.95 }}
              >
                <div className={`p-3 rounded-lg bg-gradient-to-r ${feature.color} group-hover:scale-110 transition-transform duration-300`}>
                  <feature.icon size={20} className="text-white" />
                </div>
                <span className="font-semibold text-white/90">{feature.text}</span>
              </motion.div>
            ))}
          </motion.div>

          {/* Stats */}
          <motion.div
            className="flex gap-8 pt-4"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.8 }}
          >
            <div className="text-center">
              <div className="text-2xl font-bold text-cyan-300">50K+</div>
              <div className="text-white/70 text-sm">Active Users</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-purple-300">120+</div>
              <div className="text-white/70 text-sm">Countries</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-pink-300">99%</div>
              <div className="text-white/70 text-sm">Satisfaction</div>
            </div>
          </motion.div>
        </motion.div>

        {/* Right Section - Form */}
        <motion.div
          className="relative"
          initial={{ opacity: 0, x: 50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.8, delay: 0.3 }}
        >
          {/* Glow Effect */}
          <div className="absolute -inset-4 bg-gradient-to-r from-cyan-500/20 to-purple-500/20 rounded-3xl blur-xl opacity-50"></div>
          
          <div 
            className="card-glass max-w-md mx-auto w-full relative backdrop-blur-xl bg-white/10 border border-white/20 rounded-2xl shadow-2xl"
            onMouseEnter={() => setIsHovered(true)}
            onMouseLeave={() => setIsHovered(false)}
          >
            {/* Animated Border */}
            <motion.div
              className="absolute -inset-0.5 bg-gradient-to-r from-cyan-500 to-purple-500 rounded-2xl opacity-0"
              animate={{ opacity: isHovered ? 0.7 : 0 }}
              transition={{ duration: 0.3 }}
            />

            <div className="relative p-8">
              <div className="text-center mb-8">
                {/* Form Header */}
                <motion.div
                  className="flex justify-center mb-4"
                  initial={{ scale: 0 }}
                  animate={{ scale: 1 }}
                  transition={{ delay: 0.5, type: "spring" }}
                >
                  <div className="p-3 bg-gradient-to-r from-cyan-500 to-blue-500 rounded-2xl">
                    <Zap size={28} className="text-white" />
                  </div>
                </motion.div>

                <h2 className="text-2xl font-bold text-white mb-2">
                  Join the Conversation
                </h2>
                <p className="text-white/70 text-sm">
                  Start your journey to meet amazing people
                </p>

                {/* Connection Status */}
                <motion.div
                  className={`flex items-center justify-center gap-2 text-sm mb-6 mt-4 px-4 py-2 rounded-full backdrop-blur-sm ${
                    isConnected 
                      ? 'bg-green-500/20 text-green-300 border border-green-500/30' 
                      : 'bg-red-500/20 text-red-300 border border-red-500/30'
                  }`}
                  initial={{ opacity: 0, y: 10 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: 0.7 }}
                >
                  {isConnected ? (
                    <>
                      <Wifi size={16} />
                      <span>Connected to server</span>
                      <motion.div
                        className="w-2 h-2 bg-green-400 rounded-full"
                        animate={{ scale: [1, 1.5, 1] }}
                        transition={{ duration: 2, repeat: Infinity }}
                      />
                    </>
                  ) : (
                    <>
                      <WifiOff size={16} />
                      <span>Connecting...</span>
                      <motion.div
                        className="w-2 h-2 bg-red-400 rounded-full"
                        animate={{ opacity: [1, 0.3, 1] }}
                        transition={{ duration: 1.5, repeat: Infinity }}
                      />
                    </>
                  )}
                </motion.div>
              </div>

              <form onSubmit={handleSubmit} className="space-y-6">
                <AnimatePresence mode="wait">
                  {currentStep === 1 && (
                    <motion.div
                      key="step1"
                      className="space-y-4"
                      initial={{ opacity: 0, x: 50 }}
                      animate={{ opacity: 1, x: 0 }}
                      exit={{ opacity: 0, x: -50 }}
                      transition={{ duration: 0.3 }}
                    >
                      {/* Anonymous Checkbox */}
                      <motion.div
                        className="flex items-center gap-3 p-3 rounded-xl bg-white/5 border border-white/10 hover:bg-white/10 transition-all duration-300"
                        whileHover={{ scale: 1.02 }}
                      >
                        <input
                          type="checkbox"
                          id="isAnonymous"
                          name="isAnonymous"
                          checked={formData.isAnonymous}
                          onChange={handleInputChange}
                          className="checkbox-glass text-cyan-500 focus:ring-2 focus:ring-cyan-500/50"
                        />
                        <label htmlFor="isAnonymous" className="text-white font-medium cursor-pointer flex-1">
                          Join as Anonymous
                        </label>
                        <Star size={16} className="text-yellow-400" />
                      </motion.div>

                      {/* Name Input */}
                      {!formData.isAnonymous && (
                        <motion.div
                          className="space-y-2"
                          initial={{ opacity: 0, height: 0 }}
                          animate={{ opacity: 1, height: 'auto' }}
                          transition={{ duration: 0.5 }}
                        >
                          <label htmlFor="name" className="text-white font-medium text-sm">
                            Name *
                          </label>
                          <input
                            type="text"
                            id="name"
                            name="name"
                            value={formData.name}
                            onChange={handleInputChange}
                            placeholder="Enter your name"
                            required
                            className="input-glass input-border-fix focus:input-glow w-full"
                          />
                        </motion.div>
                      )}

                      {/* Gender Selection */}
                      {!formData.isAnonymous && (
                        <motion.div
                          className="space-y-2"
                          initial={{ opacity: 0, height: 0 }}
                          animate={{ opacity: 1, height: 'auto' }}
                          transition={{ duration: 0.5, delay: 0.1 }}
                        >
                          <label htmlFor="gender" className="text-white font-medium text-sm">
                            Gender *
                          </label>
                          <select
                            id="gender"
                            name="gender"
                            value={formData.gender}
                            onChange={handleInputChange}
                            required
                            className="input-glass select-custom input-border-fix focus:input-glow w-full cursor-pointer"
                          >
                            <option value="" className="bg-gray-800 text-white">Select Gender</option>
                            <option value="male" className="bg-gray-800 text-white">Male</option>
                            <option value="female" className="bg-gray-800 text-white">Female</option>
                            <option value="other" className="bg-gray-800 text-white">Other</option>
                          </select>
                        </motion.div>
                      )}

                      {/* Next Button */}
                      <motion.button
                        type="button"
                        onClick={handleNext}
                        className="btn-primary w-full flex items-center justify-center gap-2 group relative overflow-hidden"
                        whileHover={{ scale: 1.02 }}
                        whileTap={{ scale: 0.98 }}
                      >
                        <span className="relative z-10">Next Step</span>
                        <ArrowRight size={18} className="relative z-10 group-hover:translate-x-1 transition-transform" />
                        <motion.div
                          className="absolute inset-0 bg-gradient-to-r from-cyan-500 to-blue-600"
                          whileHover={{ scale: 1.1 }}
                          transition={{ duration: 0.3 }}
                        />
                      </motion.button>
                    </motion.div>
                  )}

                  {currentStep === 2 && (
                    <motion.div
                      key="step2"
                      className="space-y-4"
                      initial={{ opacity: 0, x: 50 }}
                      animate={{ opacity: 1, x: 0 }}
                      exit={{ opacity: 0, x: -50 }}
                      transition={{ duration: 0.3 }}
                    >
                      {/* Country Input */}
                      <div className="space-y-2">
                        <label htmlFor="country" className="text-white font-medium text-sm">
                          Country (Optional)
                        </label>
                        <input
                          type="text"
                          id="country"
                          name="country"
                          value={formData.country}
                          onChange={handleInputChange}
                          placeholder="e.g., United States"
                          className="input-glass input-border-fix focus:input-glow w-full"
                        />
                      </div>

                      {/* Navigation Buttons */}
                      <div className="flex flex-col sm:flex-row gap-3">
                        <motion.button
                          type="button"
                          onClick={handleBack}
                          className="btn-secondary flex-1 flex items-center justify-center gap-2 order-2 sm:order-1 group"
                          whileHover={{ scale: 1.02 }}
                          whileTap={{ scale: 0.98 }}
                        >
                          <ArrowLeft size={18} className="group-hover:-translate-x-1 transition-transform" />
                          Back
                        </motion.button>

                        {/* Submit Button */}
                        <motion.button
                          type="submit"
                          disabled={!isConnected}
                          className="btn-primary flex-1 disabled:opacity-50 disabled:cursor-not-allowed order-1 sm:order-2 group relative overflow-hidden"
                          whileHover={{ scale: isConnected ? 1.02 : 1 }}
                          whileTap={{ scale: isConnected ? 0.98 : 1 }}
                        >
                          <span className="relative z-10">
                            {isConnected ? 'Start Chatting' : 'Connecting...'}
                          </span>
                          <motion.div
                            className="absolute inset-0 bg-gradient-to-r from-green-500 to-cyan-600"
                            whileHover={isConnected ? { scale: 1.1 } : {}}
                            transition={{ duration: 0.3 }}
                          />
                          {isConnected && (
                            <motion.div
                              className="absolute right-4"
                              animate={{ x: [0, 5, 0] }}
                              transition={{ duration: 1.5, repeat: Infinity }}
                            >
                              <Zap size={16} className="relative z-10" />
                            </motion.div>
                          )}
                        </motion.button>
                      </div>
                    </motion.div>
                  )}
                </AnimatePresence>
              </form>
            </div>
          </div>
        </motion.div>
      </div>
    </div>
  );
};

export default LandingPage;
