import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './UserLogin.css';

const UserLogin = ({ onUserJoin }) => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    gender: '',
    country: '',
    isAnonymous: false
  });
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      // Validate required fields
      if (!formData.isAnonymous && (!formData.name || !formData.email)) {
        throw new Error('Name and email are required for registered users');
      }

      if (formData.isAnonymous && !formData.name) {
        throw new Error('Name is required for anonymous users');
      }

      // Prepare user data
      const userData = {
        ...formData,
        isAnonymous: formData.isAnonymous,
        timestamp: new Date().toISOString()
      };

      // Call the parent handler
      onUserJoin(userData);

      // Navigate to waiting room
      navigate('/waiting');
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  const toggleAnonymous = () => {
    setFormData(prev => ({
      ...prev,
      isAnonymous: !prev.isAnonymous,
      email: !prev.isAnonymous ? '' : prev.email,
      password: !prev.isAnonymous ? '' : prev.password
    }));
  };

  return (
    <div className="user-login-container">
      <div className="user-login-card">
        <h2>Join VideoChat</h2>
        <form onSubmit={handleSubmit}>
          {/* Anonymous Toggle */}
          <div className="toggle-container">
            <span className={!formData.isAnonymous ? 'active' : ''}>Registered</span>
            <button type="button" onClick={toggleAnonymous} className={`toggle-btn ${formData.isAnonymous ? 'anonymous' : ''}`}>
              <span className="toggle-slider"></span>
            </button>
            <span className={formData.isAnonymous ? 'active' : ''}>Anonymous</span>
          </div>

          {/* Name Field */}
          <div className="form-group">
            <label>{formData.isAnonymous ? 'Display Name' : 'Full Name'} *</label>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              placeholder={formData.isAnonymous ? 'Enter display name' : 'Enter your full name'}
              required
            />
          </div>

          {/* Email Field - Only for registered users */}
          {!formData.isAnonymous && (
            <div className="form-group">
              <label>Email Address *</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleInputChange}
                placeholder="your@email.com"
                required
              />
            </div>
          )}

          {/* Password Field - Only for registered users */}
          {!formData.isAnonymous && (
            <div className="form-group">
              <label>Password</label>
              <input
                type="password"
                name="password"
                value={formData.password}
                onChange={handleInputChange}
                placeholder="Enter password"
              />
            </div>
          )}

          {/* Gender Field */}
          <div className="form-group">
            <label>Gender</label>
            <select
              name="gender"
              value={formData.gender}
              onChange={handleInputChange}
            >
              <option value="">Select gender (optional)</option>
              <option value="male">Male</option>
              <option value="female">Female</option>
              <option value="other">Other</option>
              <option value="prefer-not-to-say">Prefer not to say</option>
            </select>
          </div>

          {/* Country Field */}
          <div className="form-group">
            <label>Country</label>
            <input
              type="text"
              name="country"
              value={formData.country}
              onChange={handleInputChange}
              placeholder="Enter your country (optional)"
            />
          </div>

          {error && <div className="error-message">{error}</div>}

          <button type="submit" disabled={loading}>
            {loading ? 'Joining...' : 'Start Chatting'}
          </button>
        </form>

        {/* Admin Login Link */}
        <div className="admin-login-link">
          <p>Admin access? <a href="/admin/login">Admin Login</a></p>
        </div>
      </div>
    </div>
  );
};

export default UserLogin;
