import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { User, Eye, EyeOff, ArrowLeft, Mail, Lock } from 'lucide-react';

const UserLogin = ({ onUserJoin }) => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    gender: '',
    country: '',
    isAnonymous: false
  });
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      // Validate required fields
      if (!formData.isAnonymous && (!formData.name || !formData.email)) {
        throw new Error('Name and email are required for registered users');
      }

      if (formData.isAnonymous && !formData.name) {
        throw new Error('Name is required for anonymous users');
      }

      // Prepare user data
      const userData = {
        ...formData,
        isAnonymous: formData.isAnonymous,
        timestamp: new Date().toISOString()
      };

      // Call the parent handler
      onUserJoin(userData);

      // Navigate to waiting room
      navigate('/waiting');
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  const toggleAnonymous = () => {
    setFormData(prev => ({
      ...prev,
      isAnonymous: !prev.isAnonymous,
      email: !prev.isAnonymous ? '' : prev.email,
      password: !prev.isAnonymous ? '' : prev.password
    }));
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-purple-900 to-violet-900 py-16 px-4">
      <div className="max-w-md mx-auto">
        {/* Back to Home Button */}
        <button
          onClick={() => navigate('/')}
          className="mb-8 flex items-center gap-2 text-white/80 hover:text-white transition-colors duration-200"
        >
          <ArrowLeft size={20} />
          Back to Home
        </button>

        {/* Login Card */}
        <div className="glass rounded-3xl p-8 shadow-2xl">
          <div className="text-center mb-8">
            <div className="w-16 h-16 bg-gradient-to-r from-cyan-500 to-blue-600 rounded-2xl flex items-center justify-center mx-auto mb-4 shadow-lg">
              <User size={32} className="text-white" />
            </div>
            <h1 className="text-3xl font-bold text-white mb-2">Join VideoChat</h1>
            <p className="text-white/70">Connect with people around the world</p>
          </div>

          <form onSubmit={handleSubmit} className="space-y-6">
            {/* Anonymous Toggle */}
            <div className="flex items-center justify-center gap-3">
              <span className={`text-sm ${!formData.isAnonymous ? 'text-white font-medium' : 'text-white/60'}`}>
                Registered
              </span>
              <button
                type="button"
                onClick={toggleAnonymous}
                className={`relative inline-flex h-6 w-11 items-center rounded-full transition-colors ${
                  formData.isAnonymous ? 'bg-cyan-500' : 'bg-gray-600'
                }`}
              >
                <span
                  className={`inline-block h-4 w-4 transform rounded-full bg-white transition-transform ${
                    formData.isAnonymous ? 'translate-x-6' : 'translate-x-1'
                  }`}
                />
              </button>
              <span className={`text-sm ${formData.isAnonymous ? 'text-white font-medium' : 'text-white/60'}`}>
                Anonymous
              </span>
            </div>

            {/* Name Field */}
            <div>
              <label className="block text-white font-medium mb-2">
                {formData.isAnonymous ? 'Display Name' : 'Full Name'} *
              </label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleInputChange}
                className="input-glass w-full"
                placeholder={formData.isAnonymous ? 'Enter display name' : 'Enter your full name'}
                required
              />
            </div>

            {/* Email Field - Only for registered users */}
            {!formData.isAnonymous && (
              <div>
                <label className="block text-white font-medium mb-2">Email Address *</label>
                <div className="relative">
                  <input
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleInputChange}
                    className="input-glass w-full pl-12"
                    placeholder="your@email.com"
                    required
                  />
                  <Mail className="absolute left-3 top-1/2 transform -translate-y-1/2 text-white/60" size={20} />
                </div>
              </div>
            )}

            {/* Password Field - Only for registered users */}
            {!formData.isAnonymous && (
              <div>
                <label className="block text-white font-medium mb-2">Password</label>
                <div className="relative">
                  <input
                    type={showPassword ? "text" : "password"}
                    name="password"
                    value={formData.password}
                    onChange={handleInputChange}
                    className="input-glass w-full pl-12 pr-12"
                    placeholder="Enter password"
                  />
                  <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 text-white/60" size={20} />
                  <button
                    type="button"
                    onClick={() => setShowPassword(!showPassword)}
                    className="absolute right-3 top-1/2 transform -translate-y-1/2 text-white/60 hover:text-white transition-colors"
                  >
                    {showPassword ? <EyeOff size={20} /> : <Eye size={20} />}
                  </button>
                </div>
              </div>
            )}

            {/* Gender Field */}
            <div>
              <label className="block text-white font-medium mb-2">Gender</label>
              <select
                name="gender"
                value={formData.gender}
                onChange={handleInputChange}
                className="input-glass w-full"
              >
                <option value="">Select gender (optional)</option>
                <option value="male">Male</option>
                <option value="female">Female</option>
                <option value="other">Other</option>
                <option value="prefer-not-to-say">Prefer not to say</option>
              </select>
            </div>

            {/* Country Field */}
            <div>
              <label className="block text-white font-medium mb-2">Country</label>
              <input
                type="text"
                name="country"
                value={formData.country}
                onChange={handleInputChange}
                className="input-glass w-full"
                placeholder="Enter your country (optional)"
              />
            </div>

            {error && (
              <div className="bg-red-500/20 border border-red-500/30 rounded-xl p-4">
                <p className="text-red-300 text-sm">{error}</p>
              </div>
            )}

            <button
              type="submit"
              disabled={loading}
              className="btn-primary w-full text-lg py-4 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              {loading ? 'Joining...' : 'Start Chatting'}
            </button>
          </form>

          {/* Admin Login Link */}
          <div className="mt-6 text-center">
            <p className="text-white/70">
              Admin access?{' '}
              <button
                onClick={() => navigate('/admin/login')}
                className="text-orange-400 hover:text-orange-300 transition-colors font-medium"
              >
                Admin Login
              </button>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserLogin;
