import React, { useState, useEffect, useRef } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import {
  Video,
  VideoOff,
  Mic,
  MicOff,
  Phone,
  MessageCircle,
  Send,
  User,
  Globe,
  Settings,
  Wifi,
  WifiOff,
  SkipForward,
  Shield,
  Volume2,
  VolumeX,
  Crown,
  Sparkles, 
  ArrowLeft,
  Zap,
  MoreVertical
} from 'lucide-react';
import toast from 'react-hot-toast';
import ReactCountryFlag from 'react-country-flag';

// minimal country map (keep consistent with WaitingRoom)
const countryCodeMap = {
  'United States': 'US',
  'USA': 'US',
  'United Kingdom': 'GB',
  'UK': 'GB',
  'India': 'IN',
  'Canada': 'CA',
  'Australia': 'AU',
  'Germany': 'DE',
  'France': 'FR',
  'Japan': 'JP',
  'China': 'CN',
  'Brazil': 'BR',
  'Mexico': 'MX',
  'Russia': 'RU',
  'South Korea': 'KR',
  'Korea, South': 'KR',
  'Italy': 'IT',
  'Spain': 'ES',
  'Netherlands': 'NL',
  'Sweden': 'SE',
  'Norway': 'NO',
  'Denmark': 'DK',
  'Finland': 'FI',
  'Poland': 'PL',
  'Turkey': 'TR',
  'South Africa': 'ZA',
  'Israel': 'IL',
  'Thailand': 'TH',
  'Vietnam': 'VN',
  'Philippines': 'PH',
  'Indonesia': 'ID',
  'Malaysia': 'MY',
  'Singapore': 'SG',
  'New Zealand': 'NZ',
  'Ireland': 'IE',
  'Portugal': 'PT',
  'Belgium': 'BE',
  'Switzerland': 'CH',
  'Austria': 'AT',
  'Czech Republic': 'CZ',
};

// helper to get 2-letter code
const getCountryCode = (country) => {
  if (!country) return '';
  if (/^[A-Za-z]{2}$/.test(country)) return country.toUpperCase();
  return countryCodeMap[country] || '';
};

const ChatRoom = ({ socket, user }) => {
  const navigate = useNavigate();
  const location = useLocation();
  const { roomId, matchedUser: stateMatchedUser } = location.state || {};

  const [matchedUser, setMatchedUser] = useState(stateMatchedUser || null);
  const [isVideoOn, setIsVideoOn] = useState(true);
  const [isAudioOn, setIsAudioOn] = useState(true);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [isConnected, setIsConnected] = useState(false);
  const [isSkipping, setIsSkipping] = useState(false);
  const [callDuration, setCallDuration] = useState(0);
  const [showSettings, setShowSettings] = useState(false);
  const [isRemoteVideoOn, setIsRemoteVideoOn] = useState(true);

  const localVideoRef = useRef(null);
  const remoteVideoRef = useRef(null);
  const peerConnectionRef = useRef(null);
  const messagesEndRef = useRef(null);
  const callTimerRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Call timer effect
  useEffect(() => {
    if (isConnected) {
      callTimerRef.current = setInterval(() => {
        setCallDuration(prev => prev + 1);
      }, 1000);
    }

    return () => {
      if (callTimerRef.current) {
        clearInterval(callTimerRef.current);
      }
    };
  }, [isConnected]);

  useEffect(() => {
    if (!socket || !roomId) {
      navigate('/');
      return;
    }

    initializeWebRTC();

    // Socket event listeners
    socket.on('offer', handleOffer);
    socket.on('answer', handleAnswer);
    socket.on('ice-candidate', handleIceCandidate);
    socket.on('receive-message', handleReceiveMessage);
    socket.on('call-ended', handleCallEnded);
    socket.on('skip-matched', handleSkipMatched);
    socket.on('user-video-toggle', handleRemoteVideoToggle);
    socket.on('current-match', handleCurrentMatch);

    return () => {
      socket.off('offer');
      socket.off('answer');
      socket.off('ice-candidate');
      socket.off('receive-message');
      socket.off('call-ended');
      socket.off('skip-matched');
      socket.off('user-video-toggle');
      socket.off('current-match');
    };
  }, [socket, roomId]);

  const initializeWebRTC = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: {
          width: { ideal: 1280 },
          height: { ideal: 720 },
          frameRate: { ideal: 30 }
        },
        audio: {
          echoCancellation: true,
          noiseSuppression: true,
          autoGainControl: true
        }
      });

      if (localVideoRef.current) {
        localVideoRef.current.srcObject = stream;
      }

      // Create peer connection with better configuration
      peerConnectionRef.current = new RTCPeerConnection({
        iceServers: [
          { urls: 'stun:stun.l.google.com:19302' },
          { urls: 'stun:stun1.l.google.com:19302' }
        ],
        iceCandidatePoolSize: 10
      });

      // Add local stream to peer connection
      stream.getTracks().forEach(track => {
        peerConnectionRef.current.addTrack(track, stream);
      });

      // Handle remote stream
      peerConnectionRef.current.ontrack = (event) => {
        if (remoteVideoRef.current) {
          remoteVideoRef.current.srcObject = event.streams[0];
          setIsRemoteVideoOn(true);
        }
      };

      // Handle ICE candidates
      peerConnectionRef.current.onicecandidate = (event) => {
        if (event.candidate) {
          socket.emit('ice-candidate', {
            candidate: event.candidate,
            roomId: roomId
          });
        }
      };

      // Handle connection state changes
      peerConnectionRef.current.onconnectionstatechange = () => {
        const connectionState = peerConnectionRef.current.connectionState;
        if (connectionState === 'connected') {
          setIsConnected(true);
          toast.success('Connection established!');
        } else if (connectionState === 'disconnected' || connectionState === 'failed') {
          setIsConnected(false);
          toast.error('Connection lost');
        }
      };

      setIsConnected(true);
    } catch (error) {
      console.error('Error accessing media devices:', error);
      toast.error('Unable to access camera/microphone. Please check permissions.');
    }
  };

  const handleOffer = async (data) => {
    try {
      await peerConnectionRef.current.setRemoteDescription(data.offer);
      const answer = await peerConnectionRef.current.createAnswer();
      await peerConnectionRef.current.setLocalDescription(answer);
      
      socket.emit('answer', {
        answer: answer,
        roomId: roomId
      });
    } catch (error) {
      console.error('Error handling offer:', error);
    }
  };

  const handleAnswer = async (data) => {
    try {
      await peerConnectionRef.current.setRemoteDescription(data.answer);
    } catch (error) {
      console.error('Error handling answer:', error);
    }
  };

  const handleIceCandidate = async (data) => {
    try {
      await peerConnectionRef.current.addIceCandidate(data.candidate);
    } catch (error) {
      console.error('Error handling ICE candidate:', error);
    }
  };

  const handleReceiveMessage = (data) => {
    setMessages(prev => [...prev, data]);
  };

  const handleCallEnded = () => {
    toast.success('Call ended');
    navigate('/waiting');
  };

  const handleSkipMatched = (data) => {
    setIsSkipping(false);
    toast.dismiss('skipping');
    toast.success('Connected to new match!');
    navigate('/chat', { 
      state: { roomId: data.roomId, matchedUser: data.matchedUser },
      replace: true 
    });
  };

  const handleRemoteVideoToggle = (data) => {
    setIsRemoteVideoOn(data.videoOn);
  };

  const handleCurrentMatch = (data) => {
    if (data?.matchedUser) {
      setMatchedUser(data.matchedUser);
    } else {
      toast.warn('Matched user details not available');
    }
  };

  const toggleVideo = () => {
    if (localVideoRef.current?.srcObject) {
      const videoTrack = localVideoRef.current.srcObject.getVideoTracks()[0];
      if (videoTrack) {
        videoTrack.enabled = !videoTrack.enabled;
        setIsVideoOn(videoTrack.enabled);
        
        // Notify other user
        socket.emit('video-toggle', {
          roomId: roomId,
          videoOn: videoTrack.enabled
        });
      }
    }
  };

  const toggleAudio = () => {
    if (localVideoRef.current?.srcObject) {
      const audioTrack = localVideoRef.current.srcObject.getAudioTracks()[0];
      if (audioTrack) {
        audioTrack.enabled = !audioTrack.enabled;
        setIsAudioOn(audioTrack.enabled);
      }
    }
  };

  const sendMessage = () => {
    if (!newMessage.trim()) return;

    const messageData = {
      text: newMessage,
      sender: user.isAnonymous ? 'Anonymous' : user.name,
      senderId: user.id,
      timestamp: new Date().toISOString(),
      roomId: roomId
    };

    socket.emit('send-message', messageData);
    setMessages(prev => [...prev, messageData]);
    setNewMessage('');
  };

  const endCall = () => {
    if (peerConnectionRef.current) {
      peerConnectionRef.current.close();
    }
    socket.emit('end-call', { roomId: roomId });
    navigate('/waiting');
  };

  const handleBack = () => {
    navigate(-1);
  };

  const handleSkip = () => {
    if (isSkipping) return;
    setIsSkipping(true);
    socket.emit('skip-chat', { roomId: roomId });
    toast.loading('Finding your next amazing match...', { 
      id: 'skipping',
      duration: Infinity 
    });
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  const formatDuration = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  const floatingAnimation = {
    animate: {
      y: [0, -8, 0],
      transition: {
        duration: 3,
        repeat: Infinity,
        ease: "easeInOut"
      }
    }
  };

  return (
    <div className="min-h-screen flex flex-col bg-gray-100 relative overflow-hidden">
      {/* Animated Background */}
      <div className="absolute inset-0">
        <div className="absolute inset-0 bg-[radial-gradient(ellipse_at_center,_var(--tw-gradient-stops))] from-transparent via-black/30 to-black/60"></div>
        {[...Array(15)].map((_, i) => (
          <motion.div
            key={i}
            className="absolute w-1 h-1 bg-white/20 rounded-full"
            style={{
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
            }}
            animate={{
              y: [0, -100, 0],
              opacity: [0, 1, 0],
            }}
            transition={{
              duration: 3 + Math.random() * 2,
              repeat: Infinity,
              delay: Math.random() * 2,
            }}
          />
        ))}
      </div>

      {/* Header */}
      <motion.div 
        className="glass-strong px-6 py-4 flex justify-between items-center border-b border-white/20 relative z-10"
        initial={{ y: -50, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ duration: 0.5 }}
      >
        <div className="flex items-center gap-4 text-white">
          <motion.div 
            className="relative"
            whileHover={{ scale: 1.1 }}
            transition={{ duration: 0.2 }}
          >
            <div className="w-14 h-14 bg-gradient-to-r from-cyan-500 to-blue-600 rounded-2xl flex items-center justify-center text-white font-bold text-xl shadow-lg">
              {matchedUser?.isAnonymous ? 'A' : (matchedUser?.name?.[0]?.toUpperCase() || 'U')}
            </div>
            <div className="absolute -bottom-1 -right-1 w-4 h-4 bg-green-400 rounded-full border-2 border-white"></div>
          </motion.div>
          
          <div>
            <h3 className="text-xl font-bold flex items-center gap-2">
              {matchedUser?.isAnonymous ? 'Anonymous User' : matchedUser?.name || 'Unknown User'}
              <Shield size={16} className="text-cyan-400" />
            </h3>
            <div className="flex items-center gap-3 text-sm text-white/80">
              <div className="flex items-center gap-1">
                <div className="w-2 h-2 bg-green-400 rounded-full animate-pulse"></div>
                <span>Connected • {formatDuration(callDuration)}</span>
              </div>
              {matchedUser?.country && (
                <div className="flex items-center gap-1">
                  <Globe size={12} />
                  <span>{matchedUser.country}</span>
                </div>
              )}
            </div>
          </div>
        </div>

        <div className="flex items-center gap-3">
          {/* Connection Status */}
          <motion.div
            className={`flex items-center gap-2 px-3 py-2 rounded-full text-sm ${
              isConnected 
                ? 'bg-green-500/20 text-green-300 border border-green-500/30' 
                : 'bg-red-500/20 text-red-300 border border-red-500/30'
            }`}
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.5 }}
          >
            {isConnected ? <Wifi size={16} /> : <WifiOff size={16} />}
            <span>{isConnected ? 'Connected' : 'Connecting'}</span>
          </motion.div>

          {/* Control Buttons */}
          <div className="flex gap-2">
            <motion.button
              onClick={handleBack}
              className="w-12 h-12 rounded-2xl flex items-center justify-center transition-all duration-200 backdrop-blur-sm bg-white/10 hover:bg-white/20 text-white"
              whileHover={{ scale: 1.1, y: -2 }}
              whileTap={{ scale: 0.9 }}
            >
              <ArrowLeft size={20} />
            </motion.button>

            <motion.button
              onClick={toggleVideo}
              className={`w-12 h-12 rounded-2xl flex items-center justify-center transition-all duration-200 backdrop-blur-sm ${
                isVideoOn 
                  ? 'bg-green-500/90 hover:bg-green-600 text-white' 
                  : 'bg-red-500/90 hover:bg-red-600 text-white'
              }`}
              whileHover={{ scale: 1.1, y: -2 }}
              whileTap={{ scale: 0.9 }}
            >
              {isVideoOn ? <Video size={20} /> : <VideoOff size={20} />}
            </motion.button>

            <motion.button
              onClick={toggleAudio}
              className={`w-12 h-12 rounded-2xl flex items-center justify-center transition-all duration-200 backdrop-blur-sm ${
                isAudioOn 
                  ? 'bg-green-500/90 hover:bg-green-600 text-white' 
                  : 'bg-red-500/90 hover:bg-red-600 text-white'
              }`}
              whileHover={{ scale: 1.1, y: -2 }}
              whileTap={{ scale: 0.9 }}
            >
              {isAudioOn ? <Mic size={20} /> : <MicOff size={20} />}
            </motion.button>

            <motion.button
              onClick={endCall}
              className="w-12 h-12 bg-red-500/90 hover:bg-red-600 rounded-2xl flex items-center justify-center text-white transition-all duration-200 backdrop-blur-sm"
              whileHover={{ scale: 1.1, y: -2 }}
              whileTap={{ scale: 0.9 }}
            >
              <Phone size={20} />
            </motion.button>
          </div>
        </div>
      </motion.div>

      {/* Main Content */}
      <div className="flex-1 grid lg:grid-cols-3 gap-6 p-6 relative z-10">
        {/* Video Section */}
        <div className="lg:col-span-2 flex flex-col gap-6">
          {/* Remote Video */}
          <motion.div
            className="card-glass backdrop-blur-xl border border-white/20 rounded-3xl overflow-hidden relative flex-1 min-h-[500px] flex items-center justify-center"
            initial={{ scale: 0.9, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            transition={{ duration: 0.6 }}
          >
            <video
              ref={remoteVideoRef}
              autoPlay
              playsInline
              className="w-full h-full object-cover"
            />
            
            {!isRemoteVideoOn && (
              <div className="absolute inset-0 flex flex-col items-center justify-center text-white/60 text-center p-8 bg-black/40 backdrop-blur-sm">
                <VideoOff size={64} className="mb-4 text-white/40" />
                <p className="text-xl font-semibold mb-2">Camera is off</p>
                <p className="text-white/50">The other user has turned off their camera</p>
              </div>
            )}

            {/* Video Overlay Info */}
            <div className="absolute top-4 left-4 glass-strong px-3 py-2 rounded-full text-white text-sm flex items-center gap-2">
              <div className="w-2 h-2 bg-green-400 rounded-full animate-pulse"></div>
              <span>Live • {formatDuration(callDuration)}</span>
            </div>
          </motion.div>

          {/* Local Video */}
          <motion.div
            className="card-glass backdrop-blur-xl border border-white/20 rounded-2xl overflow-hidden relative w-64 h-48 self-end"
            initial={{ x: 100, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            transition={{ duration: 0.6, delay: 0.3 }}
            whileHover={{ scale: 1.05 }}
          >
            <video
              ref={localVideoRef}
              autoPlay
              muted
              playsInline
              className="w-full h-full object-cover"
            />
            
            {!isVideoOn && (
              <div className="absolute inset-0 flex items-center justify-center bg-black/40 backdrop-blur-sm">
                <VideoOff size={32} className="text-white/40" />
              </div>
            )}

            <div className="absolute bottom-2 left-2 glass-strong px-2 py-1 rounded text-white text-xs">
              You {isVideoOn ? '• Live' : '• Camera off'}
            </div>
          </motion.div>
        </div>

        {/* Chat Section */}
        <motion.div
          className="card-glass backdrop-blur-xl border border-white/20 rounded-3xl flex flex-col h-[600px] overflow-hidden"
          initial={{ x: 50, opacity: 0 }}
          animate={{ x: 0, opacity: 1 }}
          transition={{ duration: 0.6, delay: 0.4 }}
        >
          {/* Chat Header */}
          <div className="px-6 py-4 border-b border-white/20 flex items-center justify-between bg-white/5">
            <div className="flex items-center gap-3 text-white font-semibold">
              <div className="p-2 bg-gradient-to-r from-cyan-500 to-blue-600 rounded-xl">
                <MessageCircle size={20} className="text-white" />
              </div>
              <span>Live Chat</span>
            </div>
            
            <motion.button
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
              className="text-white/60 hover:text-white transition-colors"
            >
              <MoreVertical size={20} />
            </motion.button>
          </div>

          {/* Messages */}
          <div className="flex-1 p-4 overflow-y-auto scrollbar-thin space-y-4 bg-gradient-to-b from-transparent to-white/5">
            <AnimatePresence initial={false}>
              {messages.length === 0 ? (
                <motion.div
                  className="flex flex-col items-center justify-center h-full text-white/40 text-center p-8"
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                >
                  <Sparkles size={48} className="mb-4" />
                  <p className="text-lg font-semibold mb-2">Start a conversation!</p>
                  <p className="text-sm">Say hello and break the ice with your match</p>
                </motion.div>
              ) : (
                messages.map((message, index) => {
                  const isOwn = message.senderId === user.id;
                  return (
                    <motion.div
                      key={index}
                      className={`flex flex-col gap-1 ${isOwn ? 'items-end' : 'items-start'}`}
                      initial={{ opacity: 0, y: 20, scale: 0.9 }}
                      animate={{ opacity: 1, y: 0, scale: 1 }}
                      transition={{ duration: 0.3 }}
                      layout
                    >
                      <div
                        className={`px-4 py-3 rounded-2xl max-w-[85%] backdrop-blur-sm ${
                          isOwn
                            ? 'bg-gradient-to-r from-cyan-500 to-blue-600 text-white shadow-lg'
                            : 'glass-strong text-white border border-white/10'
                        }`}
                      >
                        {message.text}
                      </div>
                      <div className={`text-xs text-white/40 px-2 flex items-center gap-2 ${isOwn ? 'flex-row-reverse' : ''}`}>
                        <span>{isOwn ? 'You' : message.sender}</span>
                        <span>•</span>
                        <span>{new Date(message.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</span>
                      </div>
                    </motion.div>
                  );
                })
              )}
            </AnimatePresence>
            <div ref={messagesEndRef} />
          </div>

          {/* Message Input */}
          <div className="p-4 border-t border-white/20 space-y-3 bg-white/5">
            <div className="flex gap-3">
              <motion.input
                type="text"
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="Type your message..."
                className="flex-1 input-glass backdrop-blur-sm focus:ring-2 focus:ring-cyan-500 focus:border-transparent"
                whileFocus={{ scale: 1.02 }}
              />
              <motion.button
                onClick={sendMessage}
                disabled={!newMessage.trim()}
                className="w-12 h-12 bg-gradient-to-r from-cyan-500 to-blue-600 rounded-2xl flex items-center justify-center text-white disabled:opacity-50 disabled:cursor-not-allowed backdrop-blur-sm"
                whileHover={{ scale: newMessage.trim() ? 1.1 : 1 }}
                whileTap={{ scale: newMessage.trim() ? 0.9 : 1 }}
              >
                <Send size={20} />
              </motion.button>
            </div>
            
            <motion.button
              onClick={handleSkip}
              disabled={isSkipping}
              className="w-full py-3 bg-gradient-to-r from-purple-500 to-pink-600 hover:from-purple-600 hover:to-pink-700 rounded-2xl flex items-center justify-center gap-3 text-white font-semibold transition-all duration-200 backdrop-blur-sm disabled:opacity-50 disabled:cursor-not-allowed"
              whileHover={{ scale: isSkipping ? 1 : 1.02, y: -2 }}
              whileTap={{ scale: isSkipping ? 1 : 0.98 }}
            >
              {isSkipping ? (
                <motion.div
                  animate={{ rotate: 360 }}
                  transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
                >
                  <Zap size={18} />
                </motion.div>
              ) : (
                <SkipForward size={18} />
              )}
              {isSkipping ? 'Finding Next Match...' : 'Skip to Next Chat'}
            </motion.button>
          </div>
        </motion.div>
      </div>

      {/* Bottom Controls */}
      <motion.div
        className="flex justify-center gap-4 p-6 relative z-10"
        initial={{ y: 50, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ duration: 0.5, delay: 0.6 }}
      >
        {[
        ].map((control, index) => (
          <motion.button
            key={index}
            onClick={control.onClick}
            className={`flex items-center gap-3 px-6 py-3 rounded-2xl font-semibold transition-all duration-200 backdrop-blur-sm ${
              control.active
                ? 'bg-green-500/90 hover:bg-green-600 text-white'
                : 'bg-red-500/90 hover:bg-red-600 text-white'
            }`}
            whileHover={{ scale: 1.05, y: -2 }}
            whileTap={{ scale: 0.95 }}
          >
            <control.icon size={20} />
            <span>{control.label}</span>
          </motion.button>
        ))}
      </motion.div>
    </div>
  );
};

export default ChatRoom;
