import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import { Users, Globe, Clock, Video, MessageCircle, Wifi, WifiOff, User, MapPin } from 'lucide-react';
import toast from 'react-hot-toast';


const WaitingRoom = ({ socket, user }) => {
  const navigate = useNavigate();
  const [onlineUsers, setOnlineUsers] = useState([]);
  const [isSearching, setIsSearching] = useState(false);
  const [stats, setStats] = useState({
    totalUsers: 0,
    onlineNow: 0,
    countries: 0
  });

  useEffect(() => {
    if (!socket) return;

    socket.on('user-list-updated', (users) => {
      setOnlineUsers(users);
      setStats(prev => ({
        ...prev,
        onlineNow: users.length
      }));
    });

    socket.on('match-found', (data) => {
      setIsSearching(false);
      navigate('/chat', { state: { roomId: data.roomId, matchedUser: data.matchedUser } });
    });

    socket.on('no-match-found', () => {
      setIsSearching(false);
      toast.error('No available users found. Please try again.');
    });

    return () => {
      socket.off('user-list-updated');
      socket.off('match-found');
      socket.off('no-match-found');
    };
  }, [socket, navigate]);

  const handleFindMatch = () => {
    if (isSearching) return;
    
    setIsSearching(true);
    socket.emit('find-match', {
      gender: user.gender,
      country: user.country
    });
    
    toast.loading('Searching for a match...', { id: 'searching' });
  };

  const handleBack = () => {
    navigate('/');
  };

  return (
    <div className="min-h-screen flex items-center justify-center p-8">
      <div className="max-w-4xl w-full space-y-8">
        {/* Header */}
        <motion.div
          className="text-center space-y-4"
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
        >
          <h1 className="text-4xl lg:text-5xl font-bold text-gradient">
            Welcome to VideoChat!
          </h1>
          <p className="text-xl text-white/80 max-w-2xl mx-auto">
            You're now in the waiting room. Find someone to chat with!
          </p>
        </motion.div>

        {/* User Info Card */}
        <motion.div
          className="card-glass"
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.2 }}
        >
          <div className="text-center space-y-4">
            <div className="w-20 h-20 mx-auto bg-gradient-to-r from-primary-500 to-secondary-500 rounded-full flex items-center justify-center">
              <User size={32} className="text-white" />
            </div>
            <h3 className="text-2xl font-bold text-white">
              {user.isAnonymous ? 'Anonymous User' : user.name}
            </h3>
            <div className="flex justify-center gap-6 flex-wrap">
              {user.gender && (
                <div className="flex items-center gap-2 text-white/80">
                  <Users size={16} />
                  <span className="capitalize">{user.gender}</span>
                </div>
              )}
              {user.country && (
                <div className="flex items-center gap-2 text-white/80">
                  <MapPin size={16} />
                  <span>{user.country}</span>
                </div>
              )}
            </div>
          </div>
        </motion.div>

        {/* Stats Grid */}
        <motion.div
          className="grid grid-cols-1 md:grid-cols-3 gap-6"
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.3 }}
        >
          <motion.div
            className="card-glass text-center space-y-4"
            whileHover={{ scale: 1.05 }}
            transition={{ duration: 0.2 }}
          >
            <div className="w-16 h-16 mx-auto bg-gradient-to-r from-primary-500 to-secondary-500 rounded-full flex items-center justify-center">
              <Users size={28} className="text-white" />
            </div>
            <div className="text-3xl font-bold text-white">{stats.onlineNow}</div>
            <div className="text-white/80">Online Now</div>
          </motion.div>

          <motion.div
            className="card-glass text-center space-y-4"
            whileHover={{ scale: 1.05 }}
            transition={{ duration: 0.2 }}
          >
            <div className="w-16 h-16 mx-auto bg-gradient-to-r from-primary-500 to-secondary-500 rounded-full flex items-center justify-center">
              <Video size={28} className="text-white" />
            </div>
            <div className="text-3xl font-bold text-white">{Math.floor(stats.onlineNow / 2)}</div>
            <div className="text-white/80">Active Chats</div>
          </motion.div>

          <motion.div
            className="card-glass text-center space-y-4"
            whileHover={{ scale: 1.05 }}
            transition={{ duration: 0.2 }}
          >
            <div className="w-16 h-16 mx-auto bg-gradient-to-r from-primary-500 to-secondary-500 rounded-full flex items-center justify-center">
              <Globe size={28} className="text-white" />
            </div>
            <div className="text-3xl font-bold text-white">{new Set(onlineUsers.map(u => u.country)).size}</div>
            <div className="text-white/80">Countries</div>
          </motion.div>
        </motion.div>

        {/* Online Users */}
        {onlineUsers.length > 0 && (
          <motion.div
            className="card-glass"
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.4 }}
          >
            <h3 className="text-xl font-bold text-white mb-4 text-center">Online Users</h3>
            <div className="flex flex-wrap gap-3 justify-center">
              {onlineUsers.slice(0, 10).map((user, index) => (
                <div
                  key={index}
                  className="glass-strong px-4 py-2 rounded-full text-white text-sm flex items-center gap-2"
                >
                  <div className="w-2 h-2 bg-green-400 rounded-full"></div>
                  {user.isAnonymous ? 'Anonymous' : user.name}
                </div>
              ))}
              {onlineUsers.length > 10 && (
                <div className="glass-strong px-4 py-2 rounded-full text-white text-sm">
                  +{onlineUsers.length - 10} more
                </div>
              )}
            </div>
          </motion.div>
        )}

        {/* Loading State */}
        {isSearching && (
          <motion.div
            className="text-center space-y-4"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.3 }}
          >
            <div className="w-12 h-12 mx-auto border-4 border-white/30 border-t-white rounded-full animate-spin"></div>
            <p className="text-white/80 text-lg">
              Searching for your perfect match...
            </p>
          </motion.div>
        )}

        {/* Action Buttons */}
        <motion.div
          className="flex flex-col sm:flex-row gap-4 justify-center"
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.5 }}
        >
          <motion.button
            onClick={handleFindMatch}
            disabled={isSearching}
            className="btn-primary flex items-center gap-3 disabled:opacity-50 disabled:cursor-not-allowed"
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
          >
            <Video size={20} />
            {isSearching ? 'Searching...' : 'Find a Match'}
          </motion.button>

          <motion.button
            onClick={handleBack}
            className="btn-secondary flex items-center gap-3"
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
          >
            <MessageCircle size={20} />
            Back to Home
          </motion.button>
        </motion.div>
      </div>
    </div>
  );
};

export default WaitingRoom;
