import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { Eye, EyeOff, Shield } from 'lucide-react';
import toast from 'react-hot-toast';
import './AdminLogin.css';

const AdminLogin = ({ onLogin }) => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [remember, setRemember] = useState(true);
  const [showPassword, setShowPassword] = useState(false);
  const [loading, setLoading] = useState(false);
  const API = process.env.REACT_APP_API_URL || 'http://localhost:5000';

  const validate = () => {
    if (!email) return 'Email is required';
    // simple email check
    if (!/^\S+@\S+\.\S+$/.test(email)) return 'Enter a valid email';
    if (!password) return 'Password is required';
    return null;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const err = validate();
    if (err) {
      toast.error(err);
      return;
    }
    setLoading(true);
    try {
      const res = await fetch(`${API}/api/admin/login`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email: email.trim(), password }),
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data?.message || 'Login failed');
      const { adminUser, token } = data;
      if (!adminUser || !token) throw new Error('Invalid server response');

      // persist
      if (remember) {
        localStorage.setItem('adminToken', token);
        localStorage.setItem('adminUser', JSON.stringify(adminUser));
      } else {
        sessionStorage.setItem('adminToken', token);
        sessionStorage.setItem('adminUser', JSON.stringify(adminUser));
      }

      if (typeof onLogin === 'function') onLogin(adminUser);
      toast.success('Welcome back, Admin');
      navigate('/admin/dashboard');
    } catch (error) {
      console.error('Admin login error', error);
      toast.error(error.message || 'Login failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-[60vh] flex items-center justify-center px-4">
      <div className="w-full max-w-md bg-white/5 backdrop-blur-sm rounded-xl border border-white/10 p-6 shadow-lg">
        <div className="flex items-center gap-3 mb-4">
          <div className="w-12 h-12 rounded-lg bg-gradient-to-r from-cyan-500 to-blue-600 flex items-center justify-center text-white">
            <Shield size={20} />
          </div>
          <div>
            <h1 className="text-white text-lg font-semibold">Admin Login</h1>
            <p className="text-sm text-gray-300">Sign in to manage the platform</p>
          </div>
        </div>

        <form onSubmit={handleSubmit} className="space-y-4">
          <label className="block">
            <span className="text-sm text-gray-300">Email</span>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="mt-1 w-full p-3 rounded bg-white/5 text-white placeholder:text-gray-400 border border-transparent focus:outline-none focus:ring-2 focus:ring-purple-600"
              placeholder="admin@example.com"
              autoComplete="username"
              aria-label="Admin email"
              required
            />
          </label>

          <label className="block relative">
            <span className="text-sm text-gray-300">Password</span>
            <div className="mt-1 relative">
              <input
                type={showPassword ? 'text' : 'password'}
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="w-full p-3 pr-10 rounded bg-white/5 text-white placeholder:text-gray-400 border border-transparent focus:outline-none focus:ring-2 focus:ring-purple-600"
                placeholder="••••••••"
                autoComplete="current-password"
                aria-label="Admin password"
                required
              />
              <button
                type="button"
                onClick={() => setShowPassword((s) => !s)}
                className="absolute right-2 top-1/2 -translate-y-1/2 text-gray-300 p-1"
                aria-label={showPassword ? 'Hide password' : 'Show password'}
              >
                {showPassword ? <EyeOff size={16} /> : <Eye size={16} />}
              </button>
            </div>
          </label>

          <div className="flex items-center justify-between text-sm text-gray-300">
            <label className="inline-flex items-center gap-2">
              <input
                type="checkbox"
                checked={remember}
                onChange={(e) => setRemember(e.target.checked)}
                className="form-checkbox h-4 w-4 rounded bg-white/5 border-white/10"
                aria-label="Remember me"
              />
              Remember me
            </label>
          </div>

          <div className="flex items-center gap-3">
            <button
              type="submit"
              disabled={loading}
              className="flex-1 px-4 py-3 rounded bg-purple-600 hover:bg-purple-700 disabled:opacity-60 text-white font-medium"
            >
              {loading ? 'Signing in…' : 'Sign in'}
            </button>
            <Link
              to="/admin/register"
              className="px-4 py-3 rounded border border-white/10 text-sm text-white/90 hover:bg-white/5"
            >
              Register
            </Link>
          </div>
        </form>

        <div className="mt-4 text-xs text-gray-400">
          <p>
            By signing in you agree to the platform policies. If you have trouble, contact support.
          </p>
        </div>
      </div>
    </div>
  );
};

export default AdminLogin;
