import React, { useState, useEffect, useCallback } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { Toaster } from 'react-hot-toast';
import io from 'socket.io-client';
import toast from 'react-hot-toast';


// Components
import LandingPage from './components/LandingPage';
import ChatRoom from './components/ChatRoom';
import WaitingRoom from './components/WaitingRoom';
import ConnectionStatus from './components/ConnectionStatus';
import Header from './components/Header';


// Hooks

function App() {
  const [socket, setSocket] = useState(null);
  const [user, setUser] = useState(null);
  const [isConnected, setIsConnected] = useState(false);
  const [connectionStatus, setConnectionStatus] = useState('disconnected');
  const [reconnectAttempts, setReconnectAttempts] = useState(0);

  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
  }, []);

  // Initialize socket connection with better error handling
  useEffect(() => {
    const SOCKET_URL = process.env.REACT_APP_SOCKET_URL || 'http://localhost:5000';
    
    console.log('üîå Connecting to socket server:', SOCKET_URL);
    
    const newSocket = io(SOCKET_URL, {
      transports: ['websocket', 'polling'],
      upgrade: true,
      forceNew: true,
      timeout: 10000,
      reconnection: true,
      reconnectionAttempts: 5,
      reconnectionDelay: 1000,
      reconnectionDelayMax: 5000,
    });

    setSocket(newSocket);

    // Connection events
    newSocket.on('connect', () => {
      console.log('‚úÖ Socket connected successfully');
      setIsConnected(true);
      setConnectionStatus('connected');
      setReconnectAttempts(0);
      
      // Re-join rooms if user was previously connected
      if (user) {
        newSocket.emit('user-rejoin', user);
      }
    });

    newSocket.on('disconnect', (reason) => {
      console.log('‚ùå Socket disconnected:', reason);
      setIsConnected(false);
      setConnectionStatus('disconnected');
    });

    newSocket.on('connect_error', (error) => {
      console.error('üö® Socket connection error:', error);
      setConnectionStatus('error');
      setIsConnected(false);
    });

    newSocket.on('reconnect_attempt', (attempt) => {
      console.log(`üîÑ Reconnection attempt ${attempt}`);
      setReconnectAttempts(attempt);
      setConnectionStatus('reconnecting');
    });

    newSocket.on('reconnect_failed', () => {
      console.error('üí• Reconnection failed');
      setConnectionStatus('failed');
      setIsConnected(false);
    });

    newSocket.on('reconnect', (attempt) => {
      console.log(`‚úÖ Reconnected after ${attempt} attempts`);
      setConnectionStatus('connected');
      setIsConnected(true);
      setReconnectAttempts(0);
    });

    // Global error handler
    newSocket.on('error', (error) => {
      console.error('üö® Socket error:', error);
    });

    return () => {
      console.log('üßπ Cleaning up socket connection');
      newSocket.removeAllListeners();
      newSocket.disconnect();
    };
  }, []);

  // Handle user join with validation
  const handleUserJoin = useCallback((userData) => {
    if (!userData) {
      console.error('‚ùå No user data provided');
      return;
    }

    // Validate user data
    if (!userData.isAnonymous && (!userData.name || !userData.gender)) {
      console.error('‚ùå Invalid user data:', userData);
      return;
    }

    console.log('üë§ User joining:', {
      name: userData.isAnonymous ? 'Anonymous' : userData.name,
      gender: userData.gender,
      country: userData.country
    });

    setUser(userData);
    
    if (socket && isConnected) {
      socket.emit('user-join', {
        ...userData,
        id: socket.id,
        timestamp: new Date().toISOString()
      });
    } else {
      console.warn('‚ö†Ô∏è Socket not connected, user data saved locally');
    }
  }, [socket, isConnected]);

  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    toast.success('Logged out successfully');
  };

  // Handle socket reconnection when user exists
  useEffect(() => {
    if (socket && isConnected && user) {
      socket.emit('user-rejoin', user);
    }
  }, [socket, isConnected, user]);

  // Protected route component
  const ProtectedRoute = ({ children }) => {
    if (!user) {
      console.warn('üö´ Unauthorized access to protected route');
      return <Navigate to="/" replace />;
    }
    
    if (!isConnected) {
      console.warn('üåê No connection, redirecting to landing');
      return <Navigate to="/" replace />;
    }
    
    return children;
  };

  // Handle application errors
  useEffect(() => {
    const handleUnhandledRejection = (event) => {
      console.error('üö® Unhandled promise rejection:', event.reason);
      event.preventDefault();
    };

    const handleGlobalError = (message, source, lineno, colno, error) => {
      console.error('üö® Global error:', { message, source, lineno, colno, error });
    };

    window.addEventListener('unhandledrejection', handleUnhandledRejection);
    window.addEventListener('error', handleGlobalError);

    return () => {
      window.removeEventListener('unhandledrejection', handleUnhandledRejection);
      window.removeEventListener('error', handleGlobalError);
    };
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-purple-900 to-violet-900 flex flex-col relative overflow-hidden">
      {/* Global Connection Status */}
      <ConnectionStatus 
        status={connectionStatus}
        reconnectAttempts={reconnectAttempts}
        isConnected={isConnected}
      />

      {/* Animated Background Elements */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        <div className="absolute top-1/4 left-1/4 w-64 h-64 bg-cyan-500/10 rounded-full blur-3xl animate-float-slow"></div>
        <div className="absolute top-3/4 right-1/4 w-96 h-96 bg-purple-500/10 rounded-full blur-3xl animate-float"></div>
        <div className="absolute bottom-1/4 left-1/2 w-48 h-48 bg-blue-500/10 rounded-full blur-3xl animate-float-fast"></div>
      </div>

      <Router>
        <Header user={user} onLogout={handleLogout} />
        <Routes>
          <Route 
            path="/" 
            element={
              <LandingPage 
                onUserJoin={handleUserJoin}
                isConnected={isConnected}
                connectionStatus={connectionStatus}
                user={user}
              />
            } 
          />
          
          <Route 
            path="/waiting" 
            element={
              <ProtectedRoute>
                <WaitingRoom 
                  socket={socket}
                  user={user}
                  isConnected={isConnected}
                />
              </ProtectedRoute>
            } 
          />
          
          <Route 
            path="/chat" 
            element={
              <ProtectedRoute>
                <ChatRoom 
                  socket={socket}
                  user={user}
                  isConnected={isConnected}
                />
              </ProtectedRoute>
            } 
          />

          {/* Fallback route */}
          <Route path="*" element={<Navigate to="/" replace />} />
        </Routes>
      </Router>

      {/* Enhanced Toaster with custom styles */}
      <Toaster 
        position="top-right"
        gutter={8}
        containerClassName="!z-50"
        toastOptions={{
          duration: 4000,
          style: {
            background: 'rgba(15, 23, 42, 0.95)',
            color: '#fff',
            backdropFilter: 'blur(20px)',
            border: '1px solid rgba(255, 255, 255, 0.1)',
            borderRadius: '16px',
            padding: '12px 16px',
            fontSize: '14px',
            fontWeight: '500',
            boxShadow: '0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04)',
          },
          success: {
            iconTheme: {
              primary: '#10b981',
              secondary: '#fff',
            },
            style: {
              border: '1px solid rgba(16, 185, 129, 0.3)',
            },
          },
          error: {
            iconTheme: {
              primary: '#ef4444',
              secondary: '#fff',
            },
            style: {
              border: '1px solid rgba(239, 68, 68, 0.3)',
            },
          },
          loading: {
            iconTheme: {
              primary: '#8b5cf6',
              secondary: '#fff',
            },
            style: {
              border: '1px solid rgba(139, 92, 246, 0.3)',
            },
          },
        }}
      />

      {/* Global App Version */}
      <div className="absolute bottom-4 right-4 text-white/30 text-xs font-mono z-10">
        v{process.env.REACT_APP_VERSION || '1.0.0'}
      </div>
    </div>
  );
}

export default App;
